# Import Librariesimport pandas as pdimport os# Setting Pathprint(os.getcwd())os.chdir('/Users/ucla/Desktop/cs130/project/7/model')# Loading Datareview = pd.read_json('new-review.json', lines = True)business = pd.read_csv('business.csv')# Filtering reviews of restaurantsrestaurant_review = review[review['business_id'].isin(business.iloc[:,1])]# Writing restaurant reviews to restaurant-review.csvrestaurant_review.to_csv('restaurant_review.csv', mode='w',index=False)